// File: StockapiApplication.ktpackage com.stockinsights.stockapiimport org.springframework.boot.autoconfigure.SpringBootApplicationimport org.springframework.boot.runApplicationimport org.springframework.cache.annotation.EnableCachingimport org.springframework.context.annotation.Beanimport org.springframework.web.client.RestTemplate@EnableCaching@SpringBootApplicationclass StockapiApplication {	@Bean	fun restTemplate(): RestTemplate {		return RestTemplate()	}}fun main(args: Array<String>) {	runApplication<StockapiApplication>(*args)}